        private fun parseJson(jsonString: String): List<FastTransfer> {
            val jsonArray = JSONArray(jsonString)
            val fastTransferList = mutableListOf<FastTransfer>()

            for (i in 0 until jsonArray.length()) {
                val jsonObject = jsonArray.getJSONObject(i)
                val fastTransfer = FastTransfer()

                fastTransfer.transferStation = jsonObject.optString("transferStation")
                fastTransfer.beforeSubwayLine = jsonObject.optString("beforeSubwayLine")
                fastTransfer.beforeDirection = jsonObject.optString("beforeDirection")
                fastTransfer.afterSubwayLine = jsonObject.optString("afterSubwayLine")
                fastTransfer.afterDirection = jsonObject.optString("afterDirection")
                fastTransfer.exit = jsonObject.optString("exit")

                fastTransferList.add(fastTransfer)
            }

            return fastTransferList
        }

        fun getFastTransferExitCar(stationName: String, beforeSubwayLine: String?, afterSubwayLine: String?): Int {
            val shortStationName = stationName.substring(0, stationName.length - 1)
            val exit = getTransferData().find {
                it.transferStation == shortStationName &&
                it.beforeSubwayLine == beforeSubwayLine &&
                it.afterSubwayLine == afterSubwayLine
            }?.exit

            return exit?.toIntOrNull() ?: throw GlobalException(ErrorCode.NOT_FOUND_FAST_TRANSFER_EXIT)
        }
    }

